export type CspDirective = string[];
export interface JSONSchemaForCustomViewConfigurationFiles {
    name: string;
    description?: string;
    cloudIdentifier: string;
    mcApiUrl?: string;
    oAuthScopes: {
        view: string[];
        manage: string[];
    };
    additionalOAuthScopes?: {
        name: string;
        view: string[];
        manage: string[];
    }[];
    env: {
        development: {
            initialProjectKey: string;
            teamId?: string;
            hostUriPath?: string;
        };
        production: {
            customViewId: string;
            url: string;
            cdnUrl?: string;
        };
    };
    additionalEnv?: {
        [k: string]: unknown;
    };
    headers?: {
        csp?: {
            'connect-src': CspDirective;
            'font-src'?: CspDirective;
            'img-src'?: CspDirective;
            'script-src'?: CspDirective;
            'style-src'?: CspDirective;
            'frame-src'?: CspDirective;
        };
        permissionsPolicies?: {
            [k: string]: unknown;
        };
        strictTransportSecurity?: ('includeSubDomains' | 'preload')[];
    };
    labelAllLocales: {
        locale: 'en' | 'de' | 'es' | 'fr-FR' | 'pt-BR' | 'zh-CN';
        value: string;
    }[];
    type: 'CustomPanel';
    typeSettings?: {
        size?: 'SMALL' | 'LARGE';
        [k: string]: unknown;
    };
    locators: string[];
    [k: string]: unknown;
}
